query ChirpAppInnerIntegrationQualifyQuery(
  $reportId: String! # "reportId" = "XXXXXX" where value is Chrip Request Code
) {
  qualify (
    chirp: { requestCode: $reportId }
  ) {
    loanDecision {
      # Decision: Approve | Review | Deny
      status
      # Score Range: below - orange range, above - green range
      camApproveThreshold
      # Score Range: below - red range
      camDenyThreshold
    }
    creditApprovingDecision {
      # Score
      approvingScore
    }
    # Amount
    loanAmount
    redFlags {
      # Risk Factors
      # ----------------------------------------------------
      # LoanStopPayments: critical - red
      # LoanReversals: critical - red
      # ReversalsWithSubsequentPayments: less critical - orange
      # DebtConsolidations: critical - red
      # Collections: critical - red
      # LiquidAccount: critical - red
      # SavingsAccount: critical - red
      # BusinessAccount: critical - red
      # PrepaidAccount: critical - red
      # Disputes: critical - red
      # OtherStopPayments: non-critical - do not display
      # OtherReversals: non-critical - do not display
      # NsfOverdraftFees: non-critical - do not display
      code
    }
    income {
      primary # you need to find where "primary" = true
      # Average Income (Avg. Monthly Income)
      avgMonthly
    }
    loanInfo {
      # Outstanding Loans (Est. Total Monthly Debit)
      totalProjectedDebit
    }
  }
}

